import React from "react";
import PropTypes from "prop-types";

export class Page extends React.Component {
  onBtnClick = e => {
    const year = +e.currentTarget.innerText;
    this.props.setYear(year);
  };

  displayOutput = () => {
    const { error, photos, isFetching } = this.props;

    if (error) {
      return <p>Error in uploading of fotoes</p>;
    }

    if (isFetching) {
      return <p>LOADING....</p>;
    }
    if (photos.length) {
      return photos.map((item, index) => (
        <div className="page-photo" key={item.id}>
          <img
            src={item.sizes[1].url}
            alt="альтернативный текст"
            width="200px"
            height="200px"
          />
          <p>Likes {item.likes.count}</p>
        </div>
      ));
    } else {
      return <p>No photos....</p>;
    }
  };
  render() {
    const { year } = this.props;
    return (
      <div className="page">
        <div>
          <button className="btn" onClick={this.onBtnClick}>
            2018
          </button>
          <button className="btn" onClick={this.onBtnClick}>
            2017
          </button>
          <button className="btn" onClick={this.onBtnClick}>
            2016
          </button>
          <button className="btn" onClick={this.onBtnClick}>
            2015
          </button>
          <button className="btn" onClick={this.onBtnClick}>
            2014
          </button>
        </div>

        <p className="page-column">{year} год</p>
        {this.displayOutput()}
      </div>
    );
  }
}

Page.propTypes = {
  year: PropTypes.number.isRequired,
  photos: PropTypes.array.isRequired,
  setYear: PropTypes.func.isRequired,
  isFetching: PropTypes.bool.isRequired
};
